 function next() {
     $('.next').hide();
     $('.loader').show();
 	 var current_page = $('.page').val();
     var url = 'https://kubi-experiment.appspot.com/treatment/1';
    $.ajax({
        url: url,
        type: 'GET',
        data: {
            token: 'eyJ0eXAiOiJKV1QiLCJhbGciOiJIUzI1NiJ9.eyJpc3MiOiJsdW1lbi1qd3QiLCJzdWIiOjEsImlhdCI6MTU3NjMyMDA4MH0.vkgFJcCSQRyP0jBCpwaf1oRdvrWA1FlNBRb2lYyKi6w'
        },
        beforeSend: function (request) {
        	request.setRequestHeader("Access-Control-Allow-Origin", '*');
        	request.setRequestHeader("Access-Control-Allow-Credentials", 'true');
        	request.setRequestHeader("Content-Type", 'application/json');
        },
        dataType: 'json',
        success: function (response) {
            $.each(response.data, function (key, value) {
                // console.log(value);
                $('.datas').append(`
                    <div class="data-` + response.current_page + key + `"></div>
                `);

                $('.data-' + response.current_page + key).append(`
                        <div class="row my-4 border-bottom animated fadeIn slow data">
                            <div class="col-5">
                                <img class=" rounded w-100" src="` + value.photo + `">
                                <div class="btn-group my-2" role="group" aria-label="Basic example">
                                    <a href="` + `">
                                        <button type="button" class="btn btn-secondary btn-sm shadow-sm">Detail</button>
                                    </a>
                                    <div class="divinder mx-1"></div>
                                    <a href="` + `">
                                        <button type="button" class="btn btn-secondary btn-sm shadow-sm">Count</button>
                                    </a>
                                </div>
                            </div>
                            <div class="col-7">
                                <p class="head-title text-truncate">` + value.name + `</p>
                                <p class="desc">` + value.desc + `</p>
                            </div>
                        </div>
                    `);
            });
        },
        complete: function (data) {
            $('.loader').hide();
            $('.next').show();
            $('.desc').ellipsis({
                row: 3
            });
            // var next = data.responseJSON.current_page + 1;
            // $('.page').val(next);

            var z = JSON.parse(data.responseText);
            if (z.next_page_url != null) {
            	$('.next').show();
            } else {
            	$('.next').hide();
            }
        }
    });

 }